import React, {Component}from 'react'
import {Link} from 'react-router-dom'

import {connect} from 'react-redux'
//now using store actions 
import {createProject} from '../../store/actions/projectActions'

class CreateProject extends Component {
state={
    title:"",
    content:""
}

// this will set the state on change in input fields
handleChange = (e)=>{
    this.setState({
        [e.target.id]: e.target.value
    })
}
handleSubmit =(e) =>{
    e.preventDefault();
    // when we click on submit it will use action
    //create project 
    this.props.createProject(this.state)
}
render(){
    return(

        <div className="container">
            <form onSubmit={this.handleSubmit} className="white">
                <h5 className="grey-text text-darken-3">Create Project</h5>
                <div className="input-field">
                    <label htmlFor="title">Title</label>
                    <input type="text" id="title" onChange={this.handleChange}/>
                </div>

                <div className="input-field">
                    <label htmlFor="content">Project Content</label>
                    <textarea className="materialize-textarea" id="content" onChange={this.handleChange}/>
                </div>

                <div className="input-field">
                    <Link to={'/'}><button className="btn pink lighten-1 z-depth-0" onSubmit={this.handleSubmit}>Create</button></Link>
                </div>
            </form>
        </div>
        )
    }
}
//when submit button is clicked it will run on projectActoin
const mapDispatchToProps = (dispatch) =>{
    return{
        createProject: (project) => dispatch(createProject(project))
    }
}

export default connect(null,mapDispatchToProps)(CreateProject)
