import React from 'react';
import {compose} from 'redux'
import axios from 'axios'
import {Link} from 'react-router-dom';

export default class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [] };
  }


  componentDidMount() {
    axios.get('http://localhost:3000/api/v1/projects.json')
         .then(data => {
           let res=[]
           console.log(res)
           data.data.data.map((data) =>{

             res.push({id: data.id , title: data.title, body: data.body, user_id: data.user_id})
             this.setState({posts: res})

           })
         })
         .catch(data => {
            debugger
        })
  }

  handleDelete = (postId) => {
    fetch(`localhost:3000/api/v1/projects/${postId}`, { method: 'delete' }).
      then((response) => {
        alert('Post deleted successfully')
        this.fetchPostsList();
      });
  }

  render() {
    const { posts } = this.state;
    return (
      <div>
        <h3>All Posts</h3>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Body</th>
            </tr>
          </thead>
          <tbody>

          {
            posts.map((post) => {
              return (
                <tr key={post.id}>
                  <td>{post.id}</td>
                  <td>
                    <Link to={`/project/${post.id}`}>
                      {post.title}
                    </Link>
                  </td>
                  <td>{post.body}</td>
                  <td>
                    <button onClick={() => this.handleDelete(post.id) }>
                      Delete
                    </button>
                  </td>
                </tr>
              )
            })
          }
          </tbody>
        </table>
      </div>
    );
  }
}



















