import React, { Component } from 'react';

import index from './index'
import {BrowserRouter, Switch,Route} from 'react-router-dom'
import Navbar from './components/layout/Navbar'
import Dashboard from './components/dashboard/Dashboard'
import SignedIn from './components/auth/SignedIn'
import SignedUp from './components/auth/SignedUp'
import CreateProject from './components/projects/CreateProject'
import Projects from './components/layout/Projects'
import Project from './components/layout/Project'
import EditProject from './components/projects/EditProject';

class App extends Component {
  constructor(){
    super();
    this.state = {
      currentUser: null
    }
    this.updateCurrentUser = this.updateCurrentUser.bind(this);
  }
  componentDidMount(){
      let that = this
      axios.get('/users/check_for_user',{
      })
      .then(function(response){
        if(response.data.email){
          that.setState({
            currentUser: response.data.email
          })
        } else {
          that.setState({
            currentUser: null
          })
        }
      })
      .catch(function(error){
        console.log(error);
      })
    }
updateCurrentUser(email) {
    this.setState({
      currentUser: email
    })
  }

  render(){
    return (
      <BrowserRouter>
        <div className="App">

          <Navbar />
          <Switch>
            <Route exact path="/"  component={Projects}/>
            <Route exact path="/projects/new"  component={CreateProject}/>
            <Route exact path="/project/:id" component={Project}/>

            <Route path="/projects/:id/edit" exact component={EditProject}/>
            <Route path="/projects" component={Dashboard}/>
            <Route path="/signedin" component={SignedIn}/>
            <Route path="/signedUp" component={SignedUp}/>
            {/* <Route path="/create" component={CreateProject}/> */}

              {/* <Route exact path="/">
              <Posts />
            </Route>
            <Route
              path="/posts/:id" exact component={PostDetails}
       /> */}
          </Switch>
        </div>
      </BrowserRouter>
    );
  }
}
export default App;
