import React from 'react';
import axios from 'axios'
export default class EditProject extends React.Component {
  constructor(props) {
    super(props);
    this.state = { post: [] };

    // this.state = {
    //   title: '',
    //   des: '',
    // }
  }

  componentDidMount() {
    const { match: { params: { id } } } = this.props;
    fetch(`http://localhost:3000/api/v1/projects/${id}`).
      then((response) => response.json()).
      then((post) => this.setState({ ...post }));
  }

  // change(){
  //   const { match: { params: { id } } } = this.props;
  //   console.log({id})
  //   fetch(`http://localhost:3000/api/v1/projects/${id}`).
  //     then((response) => response.json()).
  //     then((post) => this.setState({ ...post }));

  // }
  handleInputChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  }

  updatePostRequest(){
    axios.put(`http://localhost:3000/api/v1/projects/17`, {
      method: 'put',
      body: JSON.stringify(this.state),
      headers: { 'Content-Type': 'application/json' },
    })
    .then(data => {
      console.log({data})
      let res=[]
      console.log(res)
      data.data.data.map((data) =>{

        res.push({id: data.id , title: data.title, body: data.body, user_id: data.user_id})
        this.setState({post: res})

      })
    })

  }

  render() {
    const {title, des} = this.state;
    return (

      <div>
         {/* {this.change} */}

        <h3>Update Post</h3>
        <div>
          <label>Title: </label>
          <input
            type='text'
            name='title'
            value={title}
            onChange={this.handleInputChange}
            />
        </div>
        <div>
          <label>Description: </label>
          <input
            type='text'
            name='des'
            value={des}
            onChange={this.handleInputChange}
            />
        </div>

        <button onClick={this.updatePostRequest}>Update</button>
      </div>
    );
  }
}
