import React from 'react';
import {compose} from 'redux'
import axios from 'axios'
import {Link} from 'react-router-dom';

export default class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = { posts: [] };
  }

//   componentDidMount() {
//     this.fetchPostsList();
//   }

//   fetchPostsList = () => {
//     fetch('http://localhost:3000/api/v1/projects.json').
//       then((response) => response.json()).
//       then((posts) =>  this.setState({ posts }));
//   }

  handleDelete = (postId) => {
    fetch(`http://localhost:3000/api/v1/projects/${postId}`, { method: 'delete' }).
      then((response) => {
        alert('Post deleted successfully')
        // this.fetchPostsList();
      })
  }
  componentDidMount() {
    axios.get('http://localhost:3000/api/v1/projects.json')
         .then(data => {
           let res=[]
           console.log(res)
           data.data.data.map((data) =>{

             res.push({id: data.id , title: data.title, body: data.body, user_id: data.user_id})
             this.setState({posts: res})

           })
         })
         .catch(data => {
            debugger
        })
  }


    // fetch(`localhost:3000/api/v1/projects/${postId}`, { method: 'delete' }).
    //   then((response) => {
    //     alert('Post deleted successfully')
    //     this.fetchPostsList();
    //   });


  render() {
    const { posts } = this.state;
    return (
      <div>
        <h3>All Projects</h3>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Body</th>
            </tr>
          </thead>
          <tbody>

          {
            posts.map((post) => {
              return (

            <div className="dashboard container">
                <div className="row">
                     <div className="col s6 m6">
                    <tr key={post.id}>
                        <td>{post.id}</td>
                        <td>
                        <Link to={`/project/${post.id}`}>
                        {post.title}
                        </Link>
                        </td>
                        <td>{post.body}</td>
                        <td>
                            <button><Link to={`/projects/${post.id}/edit`}>edit
                            </Link>             
                            </button>
                        </td>
                        <td>
                            <button onClick={() => this.handleDelete(post.id) }>
                            Delete
                            </button>
                        </td>
                    </tr>
                    </div>
                </div>
            </div>
              )
            })
          }
          </tbody>
        </table>
      </div>
    );
  }
}



















