import React,{Component} from 'react';
import {Link} from 'react-router-dom';
import SignedInLinks from './SignedInLinks'
import SignedOutLinks from './SignedOutLinks'
import NavbarCss from './NavbarCss.css'
import ProjectIndex from './ProjectIndex'
import axios from 'axios'
import SignedUp from '../auth/SignedUp'
import SignedIn from '../auth/SignedIn'
import Logout from '../auth/Logout'

class Navbar extends Component{
    constructor(props){
        super(props)
        if (this.props.currentUser == null){
        this.state = {
            page:"login"
        }
        } else{
        this.state = {
            page: "delete"
        }
        }
        this.changePage = this.changePage.bind(this);
    }

    changePage(newPage) {
        this.setState({
            page: newPage
        })
        }

    render(){
        switch(this.state.page) {
            case "signup":
              return <SignedUp changePage={this.changePage} updateCurrentUser={this.props.updateCurrentUser}/>
            case "login":
              return <SignedIn changePage={this.changePage} updateCurrentUser={this.props.updateCurrentUser}/>
            case "delete":
              return <Logout changePage={this.changePage} updateCurrentUser={this.props.updateCurrentUser}/>
        default:
            return(
            <div>
            <nav className="nav-wrapper grey darken-2">
                <div className= "container">
                    <Link to='/' className="brand-logo">Bugzilla</Link>
                    <SignedInLinks/>
                    <SignedOutLinks/>
                </div>
            </nav>
            </div>
        )
        }
    }
}
export default Navbar
